import React, { useState } from "react";
import axios from "axios";

const AdminItPrograms = () => {
  const [form, setForm] = useState({
    title: "",
    icon: "",
    price: "",
    description: "",
    details: "",
    technologies: ""
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/it-programs", {
        ...form,
        technologies: form.technologies.split(",").map(t => t.trim())
      });
      alert("Program added ✅");
    } catch (err) {
      console.error(err);
      alert("Error adding program ❌");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="title" placeholder="Title" onChange={handleChange} />
      <input name="icon" placeholder="Icon (emoji)" onChange={handleChange} />
      <input name="price" placeholder="Price" onChange={handleChange} />
      <input name="description" placeholder="Description" onChange={handleChange} />
      <textarea name="details" placeholder="Details" onChange={handleChange}></textarea>
      <input name="technologies" placeholder="Technologies (comma separated)" onChange={handleChange} />
      <button type="submit">Add Program</button>
    </form>
  );
};

export default AdminItPrograms;
