import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminItPrograms = () => {
  const [programs, setPrograms] = useState([]);
  const [form, setForm] = useState({
    title: "",
    icon: "",
    price: "",
    description: "",
    details: "",
    technologies: ""
  });

  useEffect(() => {
    fetchPrograms();
  }, []);

  const fetchPrograms = async () => {
    const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/it-programs`);
    setPrograms(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const techArray = form.technologies.split(",").map(t => t.trim());
    await axios.post(`${process.env.REACT_APP_API_URL}/api/it-programs`, {
      ...form,
      technologies: techArray
    });
    setForm({ title: "", icon: "", price: "", description: "", details: "", technologies: "" });
    fetchPrograms();
  };

  const handleDelete = async (id) => {
    await axios.delete(`${process.env.REACT_APP_API_URL}/api/it-programs/${id}`);
    fetchPrograms();
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Manage IT Programs</h2>
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", maxWidth: "400px" }}>
        <input placeholder="Title" value={form.title} onChange={e => setForm({ ...form, title: e.target.value })} />
        <input placeholder="Icon (emoji/url)" value={form.icon} onChange={e => setForm({ ...form, icon: e.target.value })} />
        <input placeholder="Price" value={form.price} onChange={e => setForm({ ...form, price: e.target.value })} />
        <textarea placeholder="Description" value={form.description} onChange={e => setForm({ ...form, description: e.target.value })} />
        <textarea placeholder="Details" value={form.details} onChange={e => setForm({ ...form, details: e.target.value })} />
        <input placeholder="Technologies (comma separated)" value={form.technologies} onChange={e => setForm({ ...form, technologies: e.target.value })} />
        <button type="submit" style={{ marginTop: "10px" }}>Add Program</button>
      </form>

      <h3 style={{ marginTop: "20px" }}>Existing Programs</h3>
      <ul>
        {programs.map(p => (
          <li key={p._id}>
            {p.icon} {p.title} - {p.price}
            <button onClick={() => handleDelete(p._id)} style={{ marginLeft: "10px" }}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminItPrograms;
