import React, { useState } from "react";
import { ChevronDown, ChevronUp } from "lucide-react";
import AdminItPrograms from "./AdminItPrograms";

const AdminNonItPrograms = () => (
  <div className="p-6 bg-white shadow rounded-lg">
    <h2 className="text-2xl font-bold mb-4">Non IT Services</h2>
    <p className="text-gray-600">Here you can manage Non IT Services programs.</p>
  </div>
);

export default function AdminDashboard() {
  const [openMenu, setOpenMenu] = useState(false);
  const [activePage, setActivePage] = useState("");

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-900 text-white flex flex-col">
        <div className="p-4 text-xl font-bold border-b border-gray-700">
          Admin Panel
        </div>

        <nav className="flex-1 p-4 space-y-2">
          {/* Services Dropdown */}
          <div>
            <button
              onClick={() => setOpenMenu(!openMenu)}
              className="flex items-center justify-between w-full px-3 py-2 rounded-lg hover:bg-gray-800"
            >
              <span>Services</span>
              {openMenu ? <ChevronUp size={18} /> : <ChevronDown size={18} />}
            </button>

            {openMenu && (
              <div className="ml-4 mt-2 space-y-2">
                <button
                  onClick={() => setActivePage("it")}
                  className={`block w-full text-left px-3 py-2 rounded-lg hover:bg-gray-800 ${
                    activePage === "it" ? "bg-gray-700" : ""
                  }`}
                >
                  IT Services
                </button>
                <button
                  onClick={() => setActivePage("nonit")}
                  className={`block w-full text-left px-3 py-2 rounded-lg hover:bg-gray-800 ${
                    activePage === "nonit" ? "bg-gray-700" : ""
                  }`}
                >
                  Non IT Services
                </button>
              </div>
            )}
          </div>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-6 bg-gray-50">
        {activePage === "it" && <AdminItPrograms />}
        {activePage === "nonit" && <AdminNonItPrograms />}
      </main>
    </div>
  );
}
