// src/Components/AdminSidebar.jsx
import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { ChevronDown, ChevronUp } from "lucide-react";

export default function AdminSidebar() {
  const [openServices, setOpenServices] = useState(false);
  const location = useLocation();

  return (
    <aside className="w-64 bg-gray-900 text-white flex flex-col">
      <div className="p-4 text-xl font-bold border-b border-gray-700">
        Admin Panel
      </div>

      <nav className="flex-1 p-4 space-y-2">
        {/* Dashboard Link */}
        <Link
          to="/admin/dashboard"
          className={`block px-3 py-2 rounded-lg hover:bg-gray-800 ${
            location.pathname === "/admin/dashboard" ? "bg-gray-700" : ""
          }`}
        >
          Dashboard
        </Link>

        {/* Services Dropdown */}
        <div>
          <button
            onClick={() => setOpenServices(!openServices)}
            className="flex items-center justify-between w-full px-3 py-2 rounded-lg hover:bg-gray-800"
          >
            <span>Services</span>
            {openServices ? <ChevronUp size={18} /> : <ChevronDown size={18} />}
          </button>

          {openServices && (
            <div className="ml-4 mt-2 space-y-2">
              <Link
                to="/admin/it-programs"
                className={`block px-3 py-2 rounded-lg hover:bg-gray-800 ${
                  location.pathname === "/admin/it-programs" ? "bg-gray-700" : ""
                }`}
              >
                IT Services
              </Link>
              <Link
                to="/admin/non-it-programs"
                className={`block px-3 py-2 rounded-lg hover:bg-gray-800 ${
                  location.pathname === "/admin/non-it-programs" ? "bg-gray-700" : ""
                }`}
              >
                Non IT Services
              </Link>
            </div>
          )}
        </div>
      </nav>
    </aside>
  );
}
