import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { ChevronDown, ChevronUp } from "lucide-react";
import "./AdminSidebar.css";

export default function AdminSidebar() {
  // ✅ openServices true by default
  const [openServices, setOpenServices] = useState(true);
  const location = useLocation();

  const isActive = (path) => location.pathname === path;

  return (
    <aside className="admin-sidebar">
      <div className="admin-sidebar-header">Admin Panel</div>

      <nav>
        <Link
          to="/admin/dashboard"
          className={`sidebar-link ${isActive("/admin/dashboard") ? "active" : ""}`}
        >
          Dashboard
        </Link>

        <div>
          <button
            onClick={() => setOpenServices(!openServices)}
            className="sidebar-link flex justify-between items-center"
          >
            <span>Services</span>
            {openServices ? <ChevronUp size={18} /> : <ChevronDown size={18} />}
          </button>

          {openServices && (
            <div className="submenu">
              <Link
                to="/admin/it-programs"
                className={`sidebar-link ${isActive("/admin/it-programs") ? "active" : ""}`}
              >
                IT Services
              </Link>
              <Link
                to="/admin/non-it-programs"
                className={`sidebar-link ${isActive("/admin/non-it-programs") ? "active" : ""}`}
              >
                Non IT Services
              </Link>
            </div>
          )}
        </div>
      </nav>
    </aside>
  );
}
